import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000'; // Sesuaikan dengan URL backend Anda

// Menambahkan header authorization dengan token dari local storage
const getAuthHeaders = () => ({
  headers: {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${localStorage.getItem('access_token')}`
  }
});

export const getUserById = async (userId) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/users/${userId}`, getAuthHeaders());
    return response.data; // Sesuaikan dengan struktur respons Anda
  } catch (error) {
    console.error('Failed to fetch user details', error);
    throw error;
  }
};

export const updateUser = async (userId, userData) => {
  try {
    const response = await axios.put(`${API_BASE_URL}/users/${userId}`, userData, getAuthHeaders());
    return response.data; // Sesuaikan dengan struktur respons Anda
  } catch (error) {
    console.error('Failed to update user details', error);
    throw error;
  }
};

export const fetchClasses = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/classes`, getAuthHeaders());
    return response.data.classes; // Sesuaikan dengan struktur respons Anda
  } catch (error) {
    console.error('Error fetching classes:', error);
    throw error;
  }
};

export const getAllClasses = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/classes`, getAuthHeaders());
    return response.data.classes; // Sesuaikan dengan struktur respons Anda
  } catch (error) {
    console.error('Failed to fetch classes', error);
    throw error;
  }
};

export const getClassById = async (classId) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/classes/${classId}`, getAuthHeaders());
    return response.data.class; // Sesuaikan dengan struktur respons Anda
  } catch (error) {
    console.error('Failed to fetch class details', error);
    throw error;
  }
};
