import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './Detail.css';

const Detail = () => {
  const { classId } = useParams();
  const [classData, setClassData] = useState(null);

  useEffect(() => {
    const fetchClassData = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/classes/${classId}`);
        setClassData(response.data);
      } catch (error) {
        console.error('Error fetching class data:', error);
      }
    };

    fetchClassData();
  }, [classId]);

  if (!classData) {
    return <div>Loading...</div>;
  }

  return (
    <div className='detail'>
      <div className="class-header">
        <div className="class-category">{classData.class_type}</div>
        <h1 className="class-title">{classData.title.replace(/-/g, ' ')}</h1>
        <div className="class-image">
          <img src="/images/black.jpeg" alt={classData.title} />
        </div>
      </div>
      <div className="class-body">
        <div className="class-description">
          <h2>Description</h2>
          <p>{classData.description}</p>
        </div>
        <div className="class-info">
          <ul>
            <li>Class Duration: {classData.duration}</li>
            <li>Students Enrolled: {classData.students_enrolled}</li>
            <li>Lessons: {classData.lessons}</li>
          </ul>
          <button className="btn btn-join">Login To Join Class</button>
          <div className="class-includes">
            <h3>This class includes:</h3>
            <ul>
              <li>Lifetime access</li>
              <li>Shareable certificate of completion</li>
              <li>100% online class</li>
            </ul>
          </div>
        </div>
      </div>
      <div className="class-topic">
        <h2>Class Topics</h2>
        <ul>
          {classData.topics.map((topic, index) => (
            <li key={index}>{topic}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Detail;
