import React, { useEffect, useState } from "react";
import { getJoins, deleteJoin } from "../../services/joinService";
import Navbar from "./Navbar";

const JoinedClass = () => {
  const [joins, setJoins] = useState([]);
  const [error, setError] = useState(null);
  const [message, setMessage] = useState(null);

  useEffect(() => {
    const fetchJoins = async () => {
      try {
        const student_id = localStorage.getItem("student_id");
        if (student_id) {
          const joins = await getJoins(student_id);
          setJoins(joins);
        }
      } catch (error) {
        setError("Failed to fetch joins");
      }
    };

    fetchJoins();
  }, []);

  const handleDelete = async (joins_id) => {
    try {
      await deleteJoin(joins_id);
      setMessage("Join deleted successfully");
      setJoins(joins.filter((app) => app.join_id !== joins_id));
    } catch (error) {
      setError("Failed to delete join");
    }
  };

  return (
    <div>
      <Navbar />
      <h1>My Joins</h1>
      {error && <p>{error}</p>}
      {message && <p>{message}</p>}
      <ul>
        {joins.map((join) =>
          join.class ? (
            <li key={join.join_id}>
              <h2>{join.class.class_title}</h2>
              <p>{join.class.class_description}</p>
              <p>
                <strong>Location:</strong> {join.class.location}
              </p>
              <p>
                <strong>Salary:</strong> {join.class.salary}
              </p>
              <p>
                <strong>Deadline:</strong> {join.class.join_deadline}
              </p>
              <p>
                <strong>Posted by:</strong> {join.class.employer.company_name}
              </p>
              <button onClick={() => handleDelete(join.join_id)}>Delete join</button>
            </li>
          ) : (
            <li key={join.join_id}>
              <p>class information not available</p>
              <button onClick={() => handleDelete(join.join_id)}>Delete join</button>
            </li>
          )
        )}
      </ul>
    </div>
  );
};

export default JoinedClass;
