import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getClassById } from "./classService";
import "./Detail.css";

export const Detail = () => {
  const { classId } = useParams();
  const [Class, setClass] = useState(null);
  const [error, setError] = useState(null);

  

  return (
    <div className="detail">
      <div className="class-header">
        <div className="class-category">{Class.class_type}</div>
        <h1 className="class-title">{Class.title}</h1>
        <div className="class-image">
          <img src="/images/black.jpeg" alt={Class.title} />
        </div>
      </div>
      <div className="class-body">
        <div className="class-description">
          <h2>Description</h2>
          <p>{Class.description}</p>
        </div>
        <div className="class-info">
          <ul>
            <li>Lesson: {Class.lesson}</li>
            <li>Price: {Class.price}</li>
          </ul>
          <button className="btn btn-join">Login To Join Class</button>
          <div className="class-includes">
            <h3>This class includes:</h3>
            <ul>
              <li>Lifetime access</li>
              <li>Shareable certificate of completion</li>
              <li>100% online class</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Detail;
