import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../style/ProfileTutor.css';
import Navbar from "../Tutor/NavTutor";

const ProfileTutor = () => {
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [formProfile, setFormProfile] = useState({
    tutor_phone: "",
    tutor_description: "",
    experience: ""
  });

  const fetchProfile = async () => {
    try {
      const response = await fetch('http://localhost:5000/tutor/profile', {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('access_token')}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setProfile(data);
      } else {
        throw new Error('Failed to fetch profile');
      }
    } catch (error) {
      console.error('Error fetching profile:', error);
      if (error.response && error.response.status === 401) {
        navigate('/');
      }
    }
  };

  useEffect(() => {
    fetchProfile();
  }, []);

  if (!profile) {
    return <div>Loading...</div>;
  }

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormProfile(prevState => ({
        ...prevState,
        [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
        const response = await fetch('http://localhost:5000/tutors', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem('access_token')}`
            },
            body: JSON.stringify(formProfile) 
        });

        if (response.ok) {
            fetchProfile();  // Refresh profile after saving
        } else {
            const data = await response.json();
            alert(data.message);
        }
    } catch (error) {
        console.error('Error:', error);
        alert('Something went wrong. Please try again later.');
    }
  };

  const handleCreateClass = () => {
    navigate('/create-class');
  };

  return (
    <div>
      <Navbar/>
      <div className='container'>
        <div className='profile'>
          <h1>Tutor Profile</h1>
          <p>Tutor Phone: {profile.tutor_phone}</p>
          <p>Tutor Description: {profile.tutor_description}</p>
          <p>Experience: {profile.experience}</p>
          <button className="create-class" onClick={() =>navigate(`/tutor/edit/${profile.tutor_id}`)}>CREATE CLASS</button>


          {(!profile.tutor_phone || !profile.tutor_description || !profile.experience) && (
            <div>
              <h2>Complete Your Profile</h2>
              <form onSubmit={handleSubmit}>
                {!profile.tutor_phone && (
                  <div>
                    <label htmlFor="phone-number">Phone Number</label>
                    <div className="phone-input-wrapper">
                      <input 
                        type="phone-number" 
                        name="tutor_phone" 
                        value={formProfile.tutor_phone} 
                        onChange={handleChange} 
                        placeholder="Your Phone number" 
                        required
                      />
                    </div>
                  </div>
                )}

                {!profile.tutor_description && (
                  <div>
                    <label htmlFor="tutor_description">Tutor Description</label>
                    <div className="tutor_description">
                      <textarea
                        name="tutor_description" 
                        value={formProfile.tutor_description} 
                        onChange={handleChange} 
                        placeholder="Your Description" 
                        required
                      />
                    </div>
                  </div>
                )}

                {!profile.experience && (
                  <div>
                    <label htmlFor="experience">Experience</label>
                    <div className="experience">
                      <textarea
                        name="experience" 
                        value={formProfile.experience} 
                        onChange={handleChange} 
                        placeholder="Your experience" 
                        required
                      />
                    </div>
                  </div>
                )}

                <button type="submit">Save Profile</button>
              </form>
            </div>
          )}

          <h3>My Classes</h3>
          <div className="profile-card">
            <div className="card-image">
              <img src="/images/black.jpeg" alt="Figma UI UX Design Placeholder" />
            </div>
            <p className='category'>Design</p>
            <h4>Figma UI UX Design</h4>
            <p className='desc'>Use Figma to get a job in UI Design, User Interface, User Experience design.</p>
            <button className="edit-button">Edit</button>
          </div>
          <button className="create-class" onClick={handleCreateClass}>CREATE CLASS</button>
        </div>
      </div>
    </div>
  )
}

export default ProfileTutor;
