import React, { useState } from 'react';
import Navbar from "../Student/Navbar.js";

const Dashboard = () => {
  const [users, setUsers] = useState([
    { name: 'John Doe', email: 'john@example.com', username: 'johndoe', userType: 'Admin' },
    { name: 'Jane Smith', email: 'jane@example.com', username: 'janesmith', userType: 'User' },
    { name: 'Mike Johnson', email: 'mike@example.com', username: 'mikejohnson', userType: 'User' },
    { name: 'Alice Brown', email: 'alice@example.com', username: 'alicebrown', userType: 'Admin' },
  ]);

  const [classes, setClasses] = useState([
    { title: 'Math', description: 'Basic Math', classType: 'Core', lesson: 'Math 101', price: '$50' },
    { title: 'Science', description: 'Basic Science', classType: 'Core', lesson: 'Science 101', price: '$60' },
    { title: 'History', description: 'World History', classType: 'Elective', lesson: 'History 101', price: '$40' },
    { title: 'Art', description: 'Introduction to Art', classType: 'Elective', lesson: 'Art 101', price: '$30' },
  ]);

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [isClassModalOpen, setIsClassModalOpen] = useState(false);
  const [selectedClass, setSelectedClass] = useState(null);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const handleEditClick = (user) => {
    setSelectedUser({ ...user }); // Create a copy of the user to edit
    setIsModalOpen(true);
  };

  const handleClassEditClick = (cls) => {
    setSelectedClass({ ...cls }); // Create a copy of the class to edit
    setIsClassModalOpen(true);
  };

  const handleModalClose = () => {
    setIsModalOpen(false);
    setSelectedUser(null);
    setIsClassModalOpen(false);
    setSelectedClass(null);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (selectedUser) {
      setSelectedUser((prevUser) => ({
        ...prevUser,
        [name]: value,
      }));
    } else if (selectedClass) {
      setSelectedClass((prevClass) => ({
        ...prevClass,
        [name]: value,
      }));
    }
  };

  const handleSave = () => {
    if (selectedUser) {
      setUsers((prevUsers) =>
        prevUsers.map((user) => (user.username === selectedUser.username ? selectedUser : user))
      );
    } else if (selectedClass) {
      setClasses((prevClasses) =>
        prevClasses.map((cls) => (cls.title === selectedClass.title ? selectedClass : cls))
      );
    }
    handleModalClose();
  };

  const handleAccountClick = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleLogout = () => {
    // Logic for logout
    console.log("User logged out");
  };

  return (
    <div className="dashboard">
      <div className="account-container">
        <img src="/accountt.png" alt="Account Icon" className="account-icon" onClick={handleAccountClick} />
        {isDropdownOpen && (
          <div className="dropdown">
            <button onClick={handleLogout}>Logout</button>
          </div>
        )}
      </div>
      <div className="table-section">
        <h2>User Account</h2>
        <table className="table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Username</th>
              <th>User Type</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user, index) => (
              <tr key={index}>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.username}</td>
                <td>{user.userType}</td>
                <td>
                  <button className="edit-btn" onClick={() => handleEditClick(user)}>Edit</button>
                  <button className="delete-btn">Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="table-section">
        <h2>Class Table</h2>
        <table className="table">
          <thead>
            <tr>
              <th>Title</th>
              <th>Description</th>
              <th>Class Type</th>
              <th>Lesson</th>
              <th>Price</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {classes.map((cls, index) => (
              <tr key={index}>
                <td>{cls.title}</td>
                <td>{cls.description}</td>
                <td>{cls.classType}</td>
                <td>{cls.lesson}</td>
                <td>{cls.price}</td>
                <td>
                  <button className="edit-btn" onClick={() => handleClassEditClick(cls)}>Edit</button>
                  <button className="delete-btn">Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <h2>Edit User</h2>
            <input
              type="text"
              placeholder="Name"
              name="name"
              value={selectedUser.name}
              onChange={handleInputChange}
            />
            <input
              type="email"
              placeholder="Email"
              name="email"
              value={selectedUser.email}
              onChange={handleInputChange}
            />
            <input
              type="text"
              placeholder="Username"
              name="username"
              value={selectedUser.username}
              onChange={handleInputChange}
            />
            <input type="password" placeholder="Password" />
            <button onClick={handleSave}>Save</button>
            <button onClick={handleModalClose}>Close</button>
          </div>
        </div>
      )}

      {isClassModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <h2>Edit Class</h2>
            <input
              type="text"
              placeholder="Title"
              name="title"
              value={selectedClass.title}
              onChange={handleInputChange}
            />
            <input
              type="text"
              placeholder="Description"
              name="description"
              value={selectedClass.description}
              onChange={handleInputChange}
            />
            <input
              type="text"
              placeholder="Class Type"
              name="classType"
              value={selectedClass.classType}
              onChange={handleInputChange}
            />
            <input
              type="text"
              placeholder="Lesson"
              name="lesson"
              value={selectedClass.lesson}
              onChange={handleInputChange}
            />
            <input
              type="text"
              placeholder="Price"
              name="price"
              value={selectedClass.price}
              onChange={handleInputChange}
            />
            <button onClick={handleSave}>Save</button>
            <button onClick={handleModalClose}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
