import React, { useState, useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import '../style/Dashboard.css';
import { getUsers, updateUser, deleteUser } from '../../services/userService';
import { getAllClasses, updateClass, deleteClass } from '../../services/classService';

const Dashboard = () => {
  const [users, setUsers] = useState([]);
  const [classes, setClasses] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [isClassModalOpen, setIsClassModalOpen] = useState(false);
  const [selectedClass, setSelectedClass] = useState(null);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchUsers();
    fetchClasses();
  }, []);

  const fetchUsers = async () => {
    try {
      const users = await getUsers();
      setUsers(users);
    } catch (error) {
      console.error('Failed to fetch users:', error);
    }
  };

  const fetchClasses = async () => {
    try {
      const classes = await getAllClasses();
      setClasses(classes);
    } catch (error) {
      console.error('Failed to fetch classes:', error);
    }
  };

  const handleEditClick = (user) => {
    setSelectedUser({ ...user });
    setIsModalOpen(true);
  };

  const handleClassEditClick = (cls) => {
    setSelectedClass({ ...cls });
    setIsClassModalOpen(true);
  };

  const handleModalClose = () => {
    setIsModalOpen(false);
    setSelectedUser(null);
    setIsClassModalOpen(false);
    setSelectedClass(null);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (selectedUser) {
      setSelectedUser((prevUser) => ({
        ...prevUser,
        [name]: value,
      }));
    } else if (selectedClass) {
      setSelectedClass((prevClass) => ({
        ...prevClass,
        [name]: value,
      }));
    }
  };

  const handleSave = async () => {
    try {
      if (selectedUser) {
        await updateUser(selectedUser.username, selectedUser);
        fetchUsers();
      } else if (selectedClass) {
        await updateClass(selectedClass.title, selectedClass);
        fetchClasses();
      }
      handleModalClose();
    } catch (error) {
      console.error('Failed to save:', error);
    }
  };

  const handleDeleteUser = async (username) => {
    try {
      await deleteUser(username);
      fetchUsers();
    } catch (error) {
      console.error('Failed to delete user:', error);
    }
  };

  const handleDeleteClass = async (title) => {
    try {
      await deleteClass(title);
      fetchClasses();
    } catch (error) {
      console.error('Failed to delete class:', error);
    }
  };

  const handleAccountClick = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleLogout = () => {
    console.log("User logged out");
    navigate('/')
    // Implement logout logic here
  };

  return (
    <div className="dashboard">
      <div className="account-container">
        <img src="/assets/account.png" alt="Account Icon" className="account-icon" onClick={handleAccountClick} />
        {isDropdownOpen && (
          <div className="dropdown">
            <button onClick={handleLogout}>Logout</button>
          </div>
        )}
      </div>
      <div className="table-section">
        <h2>User Account</h2>
        <table className="table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Username</th>
              <th>User Type</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user.user_id}>
                <td>{user.full_name}</td>
                <td>{user.email}</td>
                <td>{user.username}</td>
                <td>{user.user_type}</td>
                <td>
                  <button className="edit-btn" onClick={() => handleEditClick(user)}>Edit</button>
                  <button className="delete-btn" onClick={() => handleDeleteUser(user.username)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="table-section">
        <h2>Class Table</h2>
        <table className="table">
          <thead>
            <tr>
              <th>Title</th>
              <th>Description</th>
              <th>Class Type</th>
              <th>Lesson</th>
              <th>Price</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {classes.map((cls) => (
              <tr key={cls.class_id}>
                <td>{cls.title}</td>
                <td>{cls.description}</td>
                <td>{cls.class_type}</td>
                <td>{cls.lesson}</td>
                <td>{cls.price}</td>
                <td>
                  <button className="edit-btn" onClick={() => handleClassEditClick(cls)}>Edit</button>
                  <button className="delete-btn" onClick={() => handleDeleteClass(cls.title)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <h2>Edit User</h2>
            <input
              type="text"
              placeholder="Name"
              name="name"
              value={selectedUser.full_name}
              onChange={handleInputChange}
            />
            <input
              type="email"
              placeholder="Email"
              name="email"
              value={selectedUser.email}
              onChange={handleInputChange}
            />
            <input
              type="text"
              placeholder="Username"
              name="username"
              value={selectedUser.username}
              onChange={handleInputChange}
            />
            <button onClick={handleSave}>Save</button>
            <button onClick={handleModalClose}>Close</button>
          </div>
        </div>
      )}

      {isClassModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <h2>Edit Class</h2>
            <input
              type="text"
              placeholder="Title"
              name="title"
              value={selectedClass.title}
              onChange={handleInputChange}
            />
            <input
              type="text"
              placeholder="Description"
              name="description"
              value={selectedClass.description}
              onChange={handleInputChange}
            />
            <input
              type="text"
              placeholder="Class Type"
              name="classType"
              value={selectedClass.class}
              onChange={handleInputChange}
            />
            <input
              type="text"
              placeholder="Lesson"
              name="lesson"
              value={selectedClass.lesson}
              onChange={handleInputChange}
            />
            <input
              type="text"
              placeholder="Price"
              name="price"
              value={selectedClass.price}
              onChange={handleInputChange}
            />
            <button onClick={handleSave}>Save</button>
            <button onClick={handleModalClose}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
