import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getClassById } from "../../services/classService";
import { getJoins, createJoin } from "../../services/joinService";
import "../style/Detail.css";
import Navbar from "./Navbar";

export const DetailClass = () => {
  const { class_id } = useParams();
  const [Class, setClass] = useState(null);
  const [error, setError] = useState(null);
  const [message, setMessage] = useState("");
  const [hasJoined, setHasJoined] = useState(false);

  const getRandomImageUrl = (id) => `https://picsum.photos/seed/${id}/1500/300`;

  useEffect(() => {
    const fetchClass = async () => {
      try {
        const data = await getClassById(class_id);
        setClass(data);
      } catch (error) {
        setError("Failed to fetch class");
      }
    };

    const checkJoinStatus = async () => {
      try {
        const student_id = localStorage.getItem("student_id");
        if (!student_id) {
          return;
        }

        const joins = await getJoins(student_id);
        const isJoined = joins.some(join => join.class_id === class_id);
        setHasJoined(isJoined);
      } catch (error) {
        setError("Failed to check join status");
      }
    };

    fetchClass();
    checkJoinStatus();
  }, [class_id]);

  const handleJoinClass = async () => {
    try {
      const student_id = localStorage.getItem("student_id");
      if (!student_id) {
        setError("You must be logged in to join the class");
        return;
      }

      const joinData = {
        class_id,
        student_id,
        status: "Student Has Joined Class",
      };

      await createJoin(joinData);
      setMessage("Join submitted successfully");
      setHasJoined(true);
      
    } catch (error) {
      setError("Failed to join the class");
    }
  };

  if (error) {
    return <p>{error}</p>;
  }

  if (!Class) {
    return <p>Loading...</p>;
  }

  const lessonTopics = Class.lesson.split("\n");

  return (
    <div>
      <Navbar />
      <div className="detail">
        <div className="class-header">
          <div className="class-category">{Class.class_type}</div>
          <h1 className="class-title">{Class.title}</h1>
          <div className="class-image">
            <img src={getRandomImageUrl(Class.class_id)} alt={class_id} />
          </div>
        </div>
        <div className="class-body">
          <div className="class-description">
            <h2>Description</h2>
            <p>{Class.description}</p>
          </div>
          <div className="class-info">
            <ul>
              <li>Class Duration: 6 Month</li>
              <li>Students Enrolled: 30</li>
              <li>Lesson: 12</li>
            </ul>
            {!hasJoined && (
              <button className="btn btn-join" onClick={handleJoinClass}>
                Join Class
              </button>
            )}
            {message && <p>{message}</p>}
            <div className="class-includes">
              <h3>This class includes:</h3>
              <ul>
                <li>Lifetime access</li>
                <li>Shareable certificate of completion</li>
                <li>100% online class</li>
              </ul>
            </div>
          </div>
        </div>
        <div className="class-topic">
          <h2>Topic Class</h2>
          <ul>
            {lessonTopics.map((topic, index) => (
              <li key={index}>{topic}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default DetailClass;
