import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getClassById } from '../services/classService';

const ClassDetail = () => {
  const { classId } = useParams();
  const [Class, setClass] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchClass = async () => {
      try {
        const data = await getClassById(classId);
        setClass(data);
      } catch (error) {
        setError('Failed to fetch class details');
      }
    };

    fetchClass();
  }, [classId]);

  if (error) {
    return <p>{error}</p>;
  }

  if (!Class) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h1>{Class.class_title}</h1>
      <p>{Class.class_description}</p>
      <p><strong>Requirements:</strong> {Class.requirements}</p>
      <p><strong>Location:</strong> {Class.location}</p>
      <p><strong>Salary:</strong> {Class.salary}</p>
      <p><strong>Deadline:</strong> {Class.application_deadline}</p>
    </div>
  );
};

export default ClassDetail;
