import React, { useState, useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import '../style/Dashboard.css';
import { getUsers, deleteUser } from '../../services/userService';
import { getAllClasses, deleteClass } from '../../services/classService';


const Dashboard = () => {
  const [users, setUsers] = useState([]);
  const [classes, setClasses] = useState([]);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchUsers();
    fetchClasses();
  }, []);

  const fetchUsers = async () => {
    try {
      const users = await getUsers();
      setUsers(users);
    } catch (error) {
      console.error('Failed to fetch users:', error);
    }
  };

  const fetchClasses = async () => {
    try {
      const classes = await getAllClasses();
      setClasses(classes);
    } catch (error) {
      console.error('Failed to fetch classes:', error);
    }
  };

  const handleEditUser = (user_id) => {
    navigate(`/adminhome/edit-user/${user_id}`);
  };

  const handleDeleteUser = async (user_id) => {
    try {
      await deleteUser(user_id); // Panggil fungsi deleteJob dengan jobId sebagai parameter
      // Jika berhasil menghapus, perbarui daftar pekerjaan
      const updatedUsers = users.filter((user) => user.user_id !== user_id);
      setUsers(updatedUsers);
      alert("Class deleted successfully");
    } catch (error) {
      console.error("Error deleting class:", error);
      alert(error.message || "Failed to delete class");
    }
  };

  const handleEditClass = (class_id) => {
    navigate(`/adminhome/edit-class/${class_id}`);
  };

  const handleDeleteClick = async (class_id) => {
    try {
      await deleteClass(class_id); // Panggil fungsi deleteClass dengan class_id sebagai parameter
      // Jika berhasil menghapus, perbarui daftar pekerjaan
      const updatedClass = classes.filter((cls) => cls.class_id !== class_id);
      setClasses(updatedClass);
      alert("Cl deleted successfully");
    } catch (error) {
      console.error("Error deleting Cl:", error);
      alert(error.message || "Failed to delete Cl");
    }
  };

  const handleAccountClick = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  return (
    <div className="dashboard">
      <div className="account-container">
        <img src="/assets/account.png" alt="Account Icon" className="account-icon" onClick={handleAccountClick} />
        {isDropdownOpen && (
          <div className="dropdown">
            <button onClick={handleLogout}>Logout</button>
          </div>
        )}
      </div>
      <div className="table-section">
        <h2>User Account</h2>
        <table className="table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Username</th>
              <th>User Type</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user.user_id}>
                <td>{user.full_name}</td>
                <td>{user.email}</td>
                <td>{user.username}</td>
                <td>{user.user_type}</td>
                <td>
                  <button className="edit-btn" onClick={() => handleEditUser(user.user_id)}>Edit</button>
                  <button className="delete-btn" onClick={() => handleDeleteUser(user.user_id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="table-section">
        <h2>Class Table</h2>
        <table className="table">
          <thead>
            <tr>
              <th>Title</th>
              <th>Description</th>
              <th>Class Type</th>
              <th>Lesson</th>
              <th>Price</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {classes.map((cls) => (
              <tr key={cls.class_id}>
                <td>{cls.title}</td>
                <td>{cls.description}</td>
                <td>{cls.class_type}</td>
                <td>{cls.lesson}</td>
                <td>{cls.price}</td>
                <td>
                  <button className="edit-btn" onClick={() => handleClassEditClick(cls)}>Edit</button>
                  <button className="delete-btn" onClick={() => handleDeleteClass(cls.title)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {isModalOpen && selectedUser && (
        <div className="modal">
          <div className="modal-content">
            <h2>Edit User</h2>
            <input
              type="text"
              placeholder="Name"
              name="full_name"
              value={selectedUser.full_name}
              onChange={handleInputChange}
            />
            <input
              type="email"
              placeholder="Email"
              name="email"
              value={selectedUser.email}
              onChange={handleInputChange}
            />
            <input
            type="text"
            placeholder="User Type"
            name="user_type"
            value={selectedUser.user_type}
            onChange={handleInputChange}
            />
            <input
              type="text"
              placeholder="Username"
              name="username"
              value={selectedUser.username}
              onChange={handleInputChange}
            />
            <input
              type="password"
              placeholder="Password"
              name="password"
              value={selectedUser.password}
              onChange={handleInputChange}
            />
            
            <button onClick={handleSave}>Save</button>
            <button onClick={handleModalClose}>Close</button>
          </div>
        </div>
      )}

      {isClassModalOpen && selectedClass && (
        <div className="modal">
          <div className="modal-content">
            <h2>Edit Class</h2>
            <input
              type="text"
              placeholder="Title"
              name="title"
              value={selectedClass.title}
              onChange={handleInputChange}
            />
            <input
              type="text"
              placeholder="Description"
              name="description"
              value={selectedClass.description}
              onChange={handleInputChange}
            />
            <select
              name="class_type" // Ensure the select element has the correct name
              value={selectedClass.class_type} // Changed from selectedUser.class_type to selectedClass.class_type
              onChange={handleInputChange}
            >
              <option value="">Select...</option>
              {classTypes.map((type, index) => (
                <option key={index} value={type}>
                  {type}
                </option>
              ))}
            </select>
            <input
              type="text"
              placeholder="Lesson"
              name="lesson"
              value={selectedClass.lesson}
              onChange={handleInputChange}
            />
            <input
              type="text"
              placeholder="Price"
              name="price"
              value={selectedClass.price}
              onChange={handleInputChange}
            />
            <button onClick={handleSave}>Save</button>
            <button onClick={handleModalClose}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
