import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getClassById } from '../services/classService';

export const Detail = () => {
  const { classId } = useParams();
  const [Class, setClass] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchClass = async () => {
      try {
        console.log(`Fetching data for class ID: ${classId}`);
        const data = await getClassById(classId);   
        console.log('Class data:', data);
        setClass(data);
      } catch (error) {
        console.error('Error fetching class:', error);
        setError('Failed to fetch class');
      }
    };

    fetchClass();
  }, [classId]);

  if (error) {
    return <p>{error}</p>;
  }

  if (!Class) {
    return <p>Loading...</p>;
  }

  return (
    <div className='detail'>
        <div className="class-header">
            <div className="class-category">Design</div>
            {/* <h1 className="class-title">{classId.replace(/-/g, ' ')}</h1> */}
            <h1 className="class-title">{Class.class_title}</h1>
            <div className="class-image">
                <img src="/images/black.jpeg" alt={classId}/>
            </div>
        </div>
        <div className="class-body">
            <div className="class-description">
                <h2>Description</h2>
                <p>{Class.description}</p>
            </div>
            <div className="class-info">
                <ul>
                    <li>class Duration: 6 Month</li>
                    <li>Students Enrolled: 30</li>
                    <li>Lesson: 12</li>
                </ul>
                <button className="btn btn-join">Login To Join Class</button>
                <div className="class-includes">
                    <h3>This class includes:</h3>
                    <ul>
                        <li>Lifetime access</li>
                        <li>Shareable certificate of completion</li>
                        <li>100% online class</li>
                    </ul>
                </div>
            </div>
        </div>
        <div className="class-topic">
            <h2>Topic Class</h2>
            <ul>
                <li>Professional training for web designers who want to launch a freelance web design career.</li>
                <li>Comprehensive lessons on design fundamentals and UI/UX principles.</li>
                <li>Access to an exclusive online community.</li>
            </ul>
        </div>
    </div>
  )
}

export default Detail;
