import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAllClasses } from '../services/classService';

const ClassList = () => {
  const [classes, setClasses] = useState([]);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchClasses = async () => {
      try {
        const data = await getAllClasses();
        setClasses(data);
      } catch (error) {
        setError('Failed to fetch classes');
      }
    };

    fetchClasses();
  }, []);

  const handleClassClick = (classId) => {
    navigate(`/classes/${classId}`);
  };

  return (
    <div>
      <h1>Class Listings</h1>
      {error && <p>{error}</p>}
      <ul>
        {classes.map(Class => (
          <li key={Class.class_id} onClick={() => handleClassClick(Class.class_id)}>
            <h2>{Class.class_title}</h2>
            <p>{Class.class_description}</p>
            <p><strong>Lesson:</strong> {Class.lesson}</p>
            <p><strong>Price:</strong> {Class.price}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ClassList;
