import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Container, Row, Col } from 'react-bootstrap';
import { fetchClasses } from './api';
import './home.css';

const Home = () => {
  const navigate = useNavigate();
  const [classes, setClasses] = useState([]);

  useEffect(() => {
    const getClasses = async () => {
      try {
        const classesData = await fetchClasses();
        setClasses(classesData);
      } catch (error) {
        console.error('Error fetching classes:', error);
      }
    };

    getClasses();
  }, []);

  const handleExploreClasses = () => {
    navigate('/class');
  };

  const handleCardClick = (classId) => {
    navigate(`/Cladetail/${classId}`);
  };

  const getRandomImageUrl = (id) => `https://picsum.photos/seed/${id}/200/300`;

  return (
    <div className='bg'>
      <div className='header-container'>
        <div className='custom-container'>
          <div className='text-container'>
            <h1 className='custom-text'>
              The <span className='title'>Clear Path </span> To <br /> A <span className='title'>Brighter</span> <br /> Tomorrow
            </h1>
            <p className='custom-paragraph'>
              "Selamat datang di BrightPath! Kami berkomitmen untuk menyediakan
              pengalaman belajar yang interaktif dan menyenangkan. Jelajahi berbagai
              kursus dan materi yang dirancang khusus untuk meningkatkan pengetahuan
              dan keterampilan Anda. Mari mulai perjalanan belajar Anda bersama kami
              dan capai potensi penuh Anda!"
            </p>
            <button className='btn btn-light custom-button'>Get Started</button>
          </div>
          <div className='image-container'>
            <img src={getRandomImageUrl('header')} alt='BrightPath' className='right-image' />
          </div>
        </div>
      </div>
      <Row>
        <div className='content'>
          {classes.map((cls) => (
            <div key={cls.class_id} className='card' onClick={() => handleCardClick(cls.class_id)}>
              <div className='card-image-container'>
                <img src={getRandomImageUrl(cls.class_id)} alt={cls.title} />
              </div>
              <p className='card-category'>{cls.class_type}</p>
              <p className='card-name'><span>{cls.title}</span></p>
              <p>{cls.description}</p>
              <p>Lesson: {cls.lesson}</p>
              <p>Price: {cls.price}</p>
            </div>
          ))}
        </div>
      </Row>
      <Row className='explore-button-container'>
        <Col md='auto'>
          <button className='btn btn-light explore-button' onClick={handleExploreClasses}>Explore All Classes</button>
        </Col>
      </Row>
    </div>
  );
}

export default Home;
