import React, { useEffect, useState } from 'react';
import { getUserById, updateUser } from '../services/api'; // Pastikan import path sesuai
import './Profile.css';

export const Profile = () => {
  const [user, setUser] = useState({
    
  });
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const userId = localStorage.getItem('user_id'); // Asumsi user_id disimpan di localStorage
        const data = await getUserById(userId);
        setUser(data);
      } catch (error) {
        setMessage('Failed to fetch user details');
      }
    };

    fetchUser();
  }, []);

  const handleChange = (e) => {
    const { id, value } = e.target;
    setUser((prevUser) => ({ ...prevUser, [id]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const userId = localStorage.getItem('user_id');
      const updatedUser = await updateUser(userId, user);
      setUser(updatedUser);
      setMessage('Profile updated successfully!');
    } catch (error) {
      setMessage('Failed to update profile');
    }
  };

  return (
    <div className='container'>
      <div className='profile'>
        <h2>Profile</h2>
        <form onSubmit={handleSubmit}>
          <label htmlFor="fullname">Full name</label>
          <input type="text" id="fullname" value={user.fullname} onChange={handleChange} />
          <p className="user-fullname">{user.fullname}</p> {/* Display fullname here */}

          <label htmlFor="email">Email</label>
          <input type="text" id="email" value={user.email} onChange={handleChange} />

          <label htmlFor="phone">Phone Number</label>
          <input type="text" id="phone" value={user.phone} onChange={handleChange} />

          <label htmlFor="age">Age</label>
          <input type="text" id="age" value={user.age} onChange={handleChange} />

          <label htmlFor="education">Education</label>
          <input type="text" id="education" value={user.education} onChange={handleChange} />

          <button type="submit">Save Changes</button>
        </form>
        {message && <p className="message">{message}</p>}

        <h3>My Classes</h3>
        <div className="profile-card">
          <div className="card-image">
            <img src="/images/black.jpeg" alt="Figma UI UX Design Placeholder" />
          </div>
          <p className="category">Design</p>
          <h4>Figma UI UX Design</h4>
          <p className="desc">Use Figma to get a job in UI Design, User Interface, User Experience design.</p>
        </div>
      </div>
    </div>
  );
};

export default Profile;
