import React, { useEffect, useState } from 'react';
import { getUserById, updateUser, getJoinsByStudentId } from '../services/api';
import './Profile.css';

export const Profile = () => {
  const [user, setUser] = useState({
    full_name: '',
    email: '',
    phone: '',
    age: '',
    education: '',
    user_type: ''
  });
  const [message, setMessage] = useState('');
  const [joinedClasses, setJoinedClasses] = useState([]);

  useEffect(() => {
    const fetchUserAndClasses = async () => {
      try {
        const userId = localStorage.getItem('user_id');
        const userData = await getUserById(userId);
        setUser(userData);

        const classesData = await getJoinsByStudentId(userId);
        setJoinedClasses(classesData);
      } catch (error) {
        setMessage('Failed to fetch user details or classes');
      }
    };

    fetchUserAndClasses();
  }, []);

  const handleChange = (e) => {
    const { id, value } = e.target;
    setUser((prevUser) => ({ ...prevUser, [id]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const userId = localStorage.getItem('user_id');
      const updatedUser = await updateUser(userId, user);
      setUser(updatedUser);
      setMessage('Profile updated successfully!');
    } catch (error) {
      setMessage('Failed to update profile');
    }
  };

  return (
    <div className='container'>
      <div className='profile'>
        <h2>Profile</h2>
        <p>User Type: {user.user_type}</p>
        <form onSubmit={handleSubmit}>
          <label htmlFor="full_name">Full name</label>
          <input type="text" id="full_name" value={user.full_name} onChange={handleChange} />

          <label htmlFor="email">Email</label>
          <input type="text" id="email" value={user.email} onChange={handleChange} />

          <label htmlFor="phone">Phone Number</label>
          <input type="text" id="phone" value={user.phone} onChange={handleChange} />

          <label htmlFor="age">Age</label>
          <input type="text" id="age" value={user.age} onChange={handleChange} />

          <label htmlFor="education">Education</label>
          <input type="text" id="education" value={user.education} onChange={handleChange} />

          <button type="submit">Save Changes</button>
        </form>
        {message && <p className="message">{message}</p>}

        <h3>My Classes</h3>
        {joinedClasses.map((joinedClass) => (
          <div key={joinedClass.class_id} className="profile-card">
            <div className="card-image">
              <img src="/images/black.jpeg" alt={joinedClass.title} />
            </div>
            <p className="category">{joinedClass.class_type}</p>
            <h4>{joinedClass.title}</h4>
            <p className="desc">{joinedClass.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Profile;
