import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000'; // Sesuaikan dengan URL backend Anda

// Fetch all classes
export const getAllClasses = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/classes`, {
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('access_token')}`
      }
    });
    return response.data; // Sesuaikan dengan struktur respons Anda
  } catch (error) {
    console.error('Error fetching all classes:', error);
    throw error;
  }
};

// Fetch class by ID
export const getClassById = async (classId) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/classes/${classId}`, {
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('access_token')}`
      }
    });
    return response.data; // Sesuaikan dengan struktur respons Anda
  } catch (error) {
    console.error(`Error fetching class with ID ${classId}:`, error);
    throw error;
  }
};
