import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../style/ProfileTutor.css';
import Navbar from "../Tutor/NavTutor";

const ProfileTutor = () => {
  const [formProfile, setFormProfile] = useState({
    tutor_phone: '',
    tutor_description: '',
    experience: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormProfile(prevState => ({
        ...prevState,
        [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
        const response = await fetch('http://localhost:5000/tutors', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem('access_token')}`
            },
            body: JSON.stringify(formProfile) 
        });

        if (response.ok) {
            navigate('/tutorprofile');
        } else {
            const data = await response.json();
            alert(data.message);
        }
    } catch (error) {
        console.error('Error:', error);
        alert('Something went wrong. Please try again later.');
    }
  };

  
  const navigate = useNavigate();

  const handleCreateClass = () => {
    navigate('/create-class');
  };

  return (
    <div>
      <Navbar/>
      <div className='container'>
        <div className='profile'>
          <h2>Profile</h2>
          <form onSubmit={handleSubmit}>
            <label htmlFor="phone-number">Phone Number</label>
            <div className="phone-input-wrapper">
              <input 
                type="text" 
                id="phone-number" 
                name="tutor_phone" 
                placeholder="Your Phone number" 
                required
              />
            </div>

            <label htmlFor="email">Email</label>
            <input type="text" id="email" placeholder="Enter your Email" />
            
            <label htmlFor="phone-number">Phone Number</label>
            <div className="phone-input-wrapper">
              <input 
                type="text" 
                id="phone-number" 
                name="tutor_phone" 
                placeholder="Your Phone number" 
              />
            </div>

            <button type="button">Save Changes</button>
          </form>

          <h3>My Classes</h3>
          <div className="profile-card">
            <div className="card-image">
              <img src="/images/black.jpeg" alt="Figma UI UX Design Placeholder" />
            </div>
            <p className='category'>Design</p>
            <h4>Figma UI UX Design</h4>
            <p className='desc'>Use Figma to get a job in UI Design, User Interface, User Experience design.</p>
            <button className="edit-button">Edit</button>
          </div>
          <button className="create-class" onClick={handleCreateClass}>CREATE CLASS</button>
        </div>
      </div>
    </div>
  )
}

export default ProfileTutor;
