import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { getClassById} from "../../services/classService";
import { createJoin, getJoinsByStudent } from "../../services/joinService";
import "../style/Detail.css";
import Navbar from "./Navbar";

export const DetailClass = () => {
  const { class_id } = useParams();
  const [Class, setClass] = useState(null);
  const [error, setError] = useState(null);
  const [message, setMessage] = useState("");
  const [joinedClass, setJoinedClass] = useState([]);
  const navigate = useNavigate();

  const user_type = localStorage.getItem("user_type");

  const getRandomImageUrl = (id) => `https://picsum.photos/seed/${id}/1500/300`;

  useEffect(() => {
    const fetchClass = async () => {
      try {
        const data = await getClassById(class_id);
        setClass(data);
      } catch (error) {
        setError("Failed to fetch class");
      }
    };

    const fetchJoinedClass = async () => {
      try {
        const student_id = localStorage.getItem("student_id");
        if (student_id) {
          const applications = await getApplicationsBySeeker(student_id);
          const appliedJobIds = applications.map((app) => app.job_id);
          setAppliedJobs(appliedJobIds);
        }
      } catch (error) {
        setError("Failed to fetch applied jobs");
      }
    };

    fetchClass();
    fetchAppliedJobs();
  }, [class_id]);

  if (error) {
    return <p>{error}</p>;
  }

  if (!Class) {
    return <p>Loading...</p>;
  }


  

  const handleJoinClass = async () => {
    try {
      await createJoin({ class_id: class_id });
      setMessage("Successfully joined the class!");
    } catch (error) {
      setMessage("Failed to join the class");
    }
  };

  const lessonTopics = Class.lesson.split("\n");

  return (
    <div>
      <Navbar />
      <div className="detail">
        <div className="class-header">
          <div className="class-category">{Class.class_type}</div>
          <h1 className="class-title">{Class.title}</h1>
          <div className="class-image">
            <img src={getRandomImageUrl(Class.class_id)} alt={class_id} />
          </div>
        </div>
        <div className="class-body">
          <div className="class-description">
            <h2>Description</h2>
            <p>{Class.description}</p>
          </div>
          <div className="class-info">
            <ul>
              <li>class Duration: 6 Month</li>
              <li>Students Enrolled: 30</li>
              <li>Lesson: 12</li>
            </ul>
            {user_type === "student" && (
              <button className="btn btn-join" >
                Join Class
              </button>
            )}
            <div className="class-includes">
              <h3>This class includes:</h3>
              <ul>
                <li>Lifetime access</li>
                <li>Shareable certificate of completion</li>
                <li>100% online class</li>
              </ul>
            </div>
          </div>
        </div>
        <div className="class-topic">
          <h2>Topic Class</h2>
          <ul>
            {lessonTopics.map((topic, index) => (
              <li key={index}>{topic}</li>
            ))}
          </ul>
        </div>
        {message && <p>{message}</p>}
      </div>
    </div>
  );
};

export default DetailClass;
