import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getClassById, joinClass } from "../services/api";
import "./Detail.css";

export const Detail = () => {
  const { classId } = useParams();
  const [Class, setClass] = useState(null);
  const [error, setError] = useState(null);
  const [message, setMessage] = useState("");

  const getRandomImageUrl = (id) => `https://picsum.photos/seed/${id}/1500/300`;

  useEffect(() => {
    const fetchClass = async () => {
      try {
        const data = await getClassById(classId);
        setClass(data);
      } catch (error) {
        setError("Failed to fetch class");
      }
    };

    fetchClass();
  }, [classId]);

  const handleJoinClass = async () => {
    try {
      const studentId = localStorage.getItem('user_id');
      console.log('Student ID:', studentId);  // Check if user_id is being retrieved correctly
      if (!studentId) {
        setMessage("User not logged in");
        return;
      }
      const tutorId = Class.tutor_id; // Assuming this is available in the class data
      const data = {
        class_id: Class.class_id,
        student_id: studentId,
        tutor_id: tutorId
      };
      await joinClass(data);
      setMessage("Successfully joined the class!");
    } catch (error) {
      setMessage("Failed to join the class");
    }
  };

  if (error) {
    return <p>{error}</p>;
  }

  if (!Class) {
    return <p>Loading...</p>;
  }

  return (
    <div className="detail">
      <div className="class-header">
        <div className="class-category">{Class.class_type}</div>
        <h1 className="class-title">{Class.title}</h1>
        <div className="class-image">
          <img src={getRandomImageUrl(Class.class_id)} alt={classId} />
        </div>
      </div>
      <div className="class-body">
        <div className="class-description">
          <h2>Description</h2>
          <p>{Class.description}</p>
        </div>
        <div className="class-info">
          <ul>
            <li>Class Duration: 6 Month</li>
            <li>Students Enrolled: 30</li>
            <li>Lesson: 12</li>
          </ul>
          <button className="btn btn-join" onClick={handleJoinClass}>Join Class</button>
          {message && <p className="message">{message}</p>}
          <div className="class-includes">
            <h3>This class includes:</h3>
            <ul>
              <li>Lifetime access</li>
              <li>Shareable certificate of completion</li>
              <li>100% online class</li>
            </ul>
          </div>
        </div>
      </div>
      <div className="class-topic">
        <h2>Topic Class</h2>
        <ul>
          <li>{Class.lesson}</li>
        </ul>
      </div>
    </div>
  );
};

export default Detail;
