import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../style/Profile.css';
import Navbar from "./Navbar";

const Profile = () => {
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [hasProfile, setHasProfile] = useState(false);
  const [error, setError] = useState(null);
  const [classes, setClasses] = useState([]);
  const getRandomImageUrl = (id) => `https://picsum.photos/seed/${id}/200/300`;

  // Fetch profile
  const fetchProfile = async () => {
    try {
      const response = await fetch('http://localhost:5000/student/profile', {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('access_token')}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setProfile(data);
        setHasProfile(true);
      } else if (response.status === 404) {
        setHasProfile(false);
      } else {
        throw new Error('Failed to fetch profile');
      }
    } catch (error) {
      console.error('Error fetching profile:', error);
      if (error.response && error.response.status === 401) {
        navigate('/');
      }
    }
  };

  useEffect(() => {
    fetchProfile();
  }, []);

  // Fetch all classes and filter based on tutor_id
  useEffect(() => {
    if (hasProfile && profile) {
      const getClasses = async () => {
        try {
          const allClasses = await getAllClasses();
          const filteredClasses = allClasses.filter(classItem => classItem.tutor_id === profile.tutor_id);
          setClasses(filteredClasses);
        } catch (error) {
          setError("Failed to fetch classes");
        }
      };

      getClasses();
    }
  }, [hasProfile, profile]);

  if (!hasProfile) {
    return (
      <div>
        <Navbar />
        <div className='container'>
          <div className='profile'>
            <h1>Profile</h1>
            <p>You have not created a profile yet.</p>
            <button className="create-class" onClick={() => navigate('/profile/createprofile')}>Create Profile</button>
          </div>
        </div>
      </div>
    );
  }

  if (!profile) {
    return <div>Loading...</div>;
  }

  const handleCreateProfile = () => {
    navigate('/profile/createprofile');
  };

  const handleEditProfile = () => {
    navigate(`/profile/editprofile/${profile.tutor_id}`);
  };



  return (
    <div>
      <Navbar />
      <div className='container'>
        <div className='profile'>
          <h1>Profile</h1>
          <h4>Phone Number: </h4>
          <p>{profile.student_phone}</p>
          <h4>Age: </h4>
          <p>{profile.age}</p>
          <h4>Education: </h4>
          <p>{profile.education}</p>

          <button className="create-class" onClick={handleEditProfile}>Edit Profile</button>
{/*           
          <h3>My Classes</h3>
          {error ? (
            <p>{error}</p>
          ) : (
            <Row>
              <div className="content">
                {classes.map((classItem) => (
                  <div key={classItem.class_id} className="profile-card">
                    <div className="card-image">
                      <img src={getRandomImageUrl(classItem.class_id)} alt={classItem.title} />
                    </div>
                    <p className="category">{classItem.class_type}</p>
                    <h4>{classItem.title}</h4>
                    <p className="desc">{classItem.description}</p>
                    <button className="edit-button" onClick={() => handleEditClass(classItem.class_id)}>Edit Kelas</button>
                  </div>
                ))}
              </div>
            </Row>
          )}
          <button className="create-class" onClick={handleCreateClass}>CREATE CLASS</button> */}


        </div>
      </div>
    </div>
  );
};

export default Profile;
