import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import '../style/CreateClass.css';
import Navbar from "../Tutor/NavTutor";

const classTypes = [
  'Language',
  'Mathematics',
  'Science',
  'Technology',
  'Art and Design',
  'Business and Finance',
  'Music and Entertainment',
  'Life Skills',
  'Test Preparation',
  'Special Education',
  'Health and Wellness'
];

const CreateClass = () => {
  const [classTitle, setClassTitle] = useState("");
  const [classType, setClassType] = useState("");
  const [classDescription, setClassDescription] = useState("");
  const [classLesson, setClassLesson] = useState("");
  const [classPrice, setClassPrice] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    const tutor_id = localStorage.getItem("tutor_id");
    const classData = {
      tutor_id: tutor_id,
      title: jobTitle,
      class_type: jobDescription,
      requirements: requirements,
      location: location,
      salary: salary,
      job_type: jobType,
      application_deadline: applicationDeadline,
    };

    try {
      const response = await fetch("http://localhost:5000/jobs", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("access_token")}`,
        },
        body: JSON.stringify(jobData),
      });

      if (response.ok) {
        navigate("/employerHome"); 
      } else {
        const data = await response.json();
        setError(data.message || "Failed to create job");
      }
    } catch (error) {
      console.error("Error creating job:", error);
      setError("Failed to create job");
    }
  };



  return (
  <div>
    <Navbar/>
    <div className='create'>
      <h2>Class</h2>
      <p className='name'>Title</p>
      <input type="text" placeholder="Your class title" />
      <p className='name'>Descriptions</p>
      <textarea placeholder="Enter your class descriptions"/>
      <p className='name'>Class Type</p>
      <select>
          {classTypes.map((type, index) => (
            <option key={index} value={type}>{type}</option>
          ))}
      </select>
      <p className='name'>Class Lesson</p>
      <textarea type="text" placeholder="What is primarily taught in your Class?" />
      <p className='name'>Price</p>
      <input type="text" placeholder="Rp.xxx.xxx" />
      {/* <select>
        <option>Class durations</option>
      </select> */}
      {/* <div className="thumbnail-container">
        <img src='/images/black.jpeg' alt="Thumbnail placeholder" />
        <div>
          <p>
            Upload your course Thumbnail here. <strong>Important guidelines:</strong> 1200x800 pixels or 12:8 Ratio. Supported format: .jpg, .jpeg, or .png
          </p>
          <button>Upload Image</button>
        </div>
      </div> */}
      <button>Save</button>
    </div>
  </div>
  );
}

export default CreateClass;
