import React, { useEffect, useState } from "react";
import { Row } from "react-bootstrap";

import { getJoins, deleteJoin } from "../../services/joinService";
import Navbar from "./Navbar";
// import { getJoinsByStudent } from "../../services/joinService";

const JoinedClass = () => {
  const [joins, setJoins] = useState([]);
  const [joinedClass, setJoinedClass] = useState([]);
  const [error, setError] = useState(null);
  const [message, setMessage] = useState(null);

  useEffect(() => {
    const fetchJoins = async () => {
      try {
        const student_id = localStorage.getItem("student_id");
        if (student_id) {
          const joins = await getJoins(student_id);
          setJoins(joins);
        }
      } catch (error) {
        setError("Failed to fetch joins");
      }
    };

    // const fetchJoinedClass = async () => {
    //   try {
    //     const student_id = localStorage.getItem("student_id");
    //     if (student_id) {
    //       const joins = await getJoinsByStudent(student_id);
    //       const joinedClassId = joins.map((app) => app.class_id);
    //       setJoinedClass(joinedClassId);
    //     }
    //   } catch (error) {
    //     setError("Failed to fetch joined class");
    //   }
    // };

    fetchJoins();
    // fetchJoinedClass();
  }, []);

  const handleDelete = async (joins_id) => {
    try {
      await deleteJoin(joins_id);
      setMessage("Join deleted successfully");
      setJoins(joins.filter((app) => app.join_id !== joins_id));
    } catch (error) {
      setError("Failed to delete join");
    }
  };



  // return (
  //   <div>
  //     <Navbar/>
  //     <div className="classes">
  //       <h1>Class</h1>
  //       {error && <p>{error}</p>}
  //       <Row>
  //         <div className="content">
  //           {classes.map((cls) => (
  //             <div
  //               key={cls.class_id}
  //               className="card"
  //               onClick={() => handleCardClick(cls.class_id)}
  //             >
  //               <div className="card-image-container">
  //                 <img src={getRandomImageUrl(cls.class_id)} alt={cls.title} />
  //               </div>
  //               <p className="card-category">{cls.class_type}</p>
  //               <p className="card-name">
  //                 <span>{cls.title}</span>
  //               </p>
  //               <p className="card-desc">{cls.description}</p>
  //               <p>Price: {cls.price}</p>
  //             </div>
  //           ))}
  //         </div>
  //       </Row>
  //     </div>
  //   </div>
  //   );

  return (
    <div>
      <Navbar />
      <h1>Class Joined</h1>
      {error && <p>{error}</p>}
      {message && <p>{message}</p>}
      <Row>
        <ul>
          {joins.map((join) =>
            join.class ? (
              <li key={join.join_id}>
                <h2>{join.class.title}</h2>
                <p>{join.class.description}</p>
                <p>
                  <strong>Class Type:</strong> {join.class.class_type}
                </p>
                <p>
                  <strong>Lesson:</strong> {join.class.lesson}
                </p>
                <p>
                  <strong>Price:</strong> {join.class.price}
                </p>
                <button onClick={() => handleDelete(join.join_id)}>Delete join</button>
              </li>
            ) : (
              <li key={join.join_id}>
                <p>Class information not available</p>
                <button onClick={() => handleDelete(join.join_id)}>Delete join</button>
              </li>
            )
          )}
        </ul>
      </Row>
    </div>
  );
};

export default JoinedClass;
